				break;

			found[0] |= (1 << 3);
			qmi_set(res, band_preference, le64_to_cpu(*(uint64_t *) get_next(8)));
			break;

		case 0x13:
			if (found[0] & (1 << 4))
				break;

			found[0] |= (1 << 4);
			qmi_set(res, cdma_prl_preference, le16_to_cpu(*(uint16_t *) get_next(2)));
			break;

